  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { PropsTable, Playground } from 'docz'
import { State } from 'react-powerplug';
import Dropdown from 'react-components/shared/dropdown';
import 'styles/_docz.scss';

const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}>
<MDXTag name="h2" components={components} props={{"id":"dropdown"}}>{`Dropdown`}</MDXTag>
<div className="components-container -spaced -width-200">
  <State initial={{ options:
    [{ label: 'Blue', value: 'blue' },
      { label: 'Red', value: 'red' },
      { label: 'Green', value: 'green' }],
      value: { label: 'Red', value: 'red' }
    }}>
    {({ state, setState }) => (
      <Dropdown
        options={state.options}
        value={state.value}
        onChange={value => setState({ value })}
      />
    )}
  </State>
</div>
<MDXTag name="h4" components={components} props={{"id":"with-caret-arrowtype-and-position-up"}}>{`With 'caret' arrowType and position up`}</MDXTag>
<div className="components-container -spaced -width-200">
  <State initial={{ options:
    [{ label: 'Blue', value: 'blue' },
      { label: 'Red', value: 'red' },
      { label: 'Green', value: 'green' }],
      value: { label: 'Red', value: 'red' }
    }}>
    {({ state, setState }) => (
      <Dropdown
        options={state.options}
        value={state.value}
        onChange={value => setState({ value })}
        arrowType='caret'
        position="up"
      />
    )}
  </State>
</div>
<MDXTag name="h4" components={components} props={{"id":"with-icon-prop-in-the-options"}}>{`With icon prop in the options`}</MDXTag>
<div className="components-container -spaced -width-200">
  <State initial={{ options:
    [{ label: 'Blue', value: 'blue', icon: 'close' },
      { label: 'Red', value: 'red', icon: 'close' },
      { label: 'Green', value: 'green', icon: 'close' }],
      value: { label: 'Red', value: 'red', icon: 'close' }
    }}>
    {({ state, setState }) => (
      <Dropdown
        options={state.options}
        value={state.value}
        onChange={value => setState({ value })}
        arrowType='caret'
      />
    )}
  </State>
</div>
<MDXTag name="h4" components={components} props={{"id":"fitcontent-prop-to-adjust-to-selected-content-length"}}>{`fitContent prop to adjust to selected content length`}</MDXTag>
<div className="components-container -spaced">
  <State initial={{ options:
    [{ label: 'Blue', value: 'blue' },
      { label: 'Red', value: 'red' },
      { label: 'Green', value: 'green' }],
      value: { label: 'Red', value: 'red' }
    }}>
    {({ state, setState }) => (
      <Dropdown
        options={state.options}
        value={state.value}
        onChange={value => setState({ value })}
        arrowType='caret'
        fitContent
      />
    )}
  </State>
</div>
<MDXTag name="h4" components={components} props={{"id":"only-view-dropdown"}}>{`Only view dropdown`}</MDXTag>
<div className="components-container -spaced">
  <State initial={{ options:
    [{ label: 'Blue', value: 'blue' },
      { label: 'Red', value: 'red' },
      { label: 'Green', value: 'green' }],
      value: { label: 'Red', value: 'red' }
    }}>
    {({ state, setState }) => (
      <Dropdown
        options={state.options}
        value={state.value}
        onChange={value => setState({ value })}
        fitContent
        selectedValueOverride={`${state.options.length} items`}
        showSelected
        readOnly
      />
    )}
  </State>
</div>
