  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { PropsTable, Playground } from 'docz'
import { State } from 'react-powerplug';
import GridList from 'react-components/shared/grid-list/grid-list.component';
import GridListItem from 'react-components/shared/grid-list-item/grid-list-item.component';
import 'styles/_docz.scss';
import countries from './mock-data/countries-list.json';
import indicators from './mock-data/indicators-list.json';

const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}>
<MDXTag name="h2" components={components} props={{"id":"grid-list"}}>{`Grid List`}</MDXTag>
<div className="components-container" style={{ width: 760 }}>
  <State initial={{ active: [] }}>
    {({ state, setState }) => (
      <GridList
          width={760}
          height={200}
          rowHeight={50}
          columnWidth={190}
          columnCount={4}
          items={countries}
        >
          {itemProps => (
            <GridListItem
              {...itemProps}
              isActive={state.active.includes(itemProps.item && itemProps.item.id)}
              enableItem={() => setState({ active: [...state.active, itemProps.item.id] })}
              disableItem={() => setState({ active: state.active.filter(i => i !== itemProps.item.id) })}
            />
          )}
        </GridList>
    )}
  </State>
</div>
<MDXTag name="h3" components={components} props={{"id":"items-with-tooltips"}}>{`Items with tooltips`}</MDXTag>
<div className="components-container" style={{ width: 760 }}>
  <State initial={{ active: [], activeInfo: null }}>
      {({ state, setState }) => (
        <GridList
            width={760}
            height={150}
            rowHeight={50}
            columnWidth={190}
            columnCount={4}
            items={countries.slice(0, 16)}
          >
            {itemProps => (
              <GridListItem
                {...itemProps}
                tooltip={`Lorem ipsum dolor sit ${itemProps.item.id}`}
                isInfoActive={state.activeInfo === itemProps.item.id}
                onInfoClick={it => setState({ activeInfo: state.activeInfo === it.id ? null : it.id })}
                isActive={state.active.includes(itemProps.item && itemProps.item.id)}
                enableItem={() => setState({ active: [...state.active, itemProps.item.id] })}
                disableItem={() => setState({ active: state.active.filter(i => i !== itemProps.item.id) })}
              />
            )}
          </GridList>
      )}
    </State>
</div>
<MDXTag name="h3" components={components} props={{"id":"items-by-groups"}}>{`Items by groups`}</MDXTag>
<div className="components-container" style={{ width: 760 }}>
  <State initial={{ active: [] }}>
      {({ state, setState }) => (
        <GridList
            width={760}
            height={200}
            rowHeight={50}
            columnWidth={190}
            columnCount={4}
            groupBy="group"
            items={indicators}
          >
            {itemProps => (
              <GridListItem
                {...itemProps}
                isActive={state.active.includes(itemProps.item && itemProps.item.id)}
                enableItem={() => setState({ active: [...state.active, itemProps.item.id] })}
                disableItem={() => setState({ active: state.active.filter(i => i !== itemProps.item.id) })}
              />
            )}
          </GridList>
      )}
    </State>
</div>
<MDXTag name="h3" components={components} props={{"id":"loading-state"}}>{`Loading state`}</MDXTag>
<div className="components-container" style={{ width: 760 }}>
  <GridList
    width={760}
    height={100}
    rowHeight={50}
    columnWidth={190}
    columnCount={4}
    items={countries.slice(0, 16)}
    loading
  >
    {item => (
      <GridListItem
        {...item}
        enableItem={() => {}}
        disableItem={() => {}}
      />
    )}
  </GridList>
</div>
<MDXTag name="h3" components={components} props={{"id":"disabled-state"}}>{`Disabled state`}</MDXTag>
<div className="components-container" style={{ width: 760 }}>
  <GridList
    width={760}
    height={100}
    rowHeight={50}
    columnWidth={190}
    columnCount={4}
    items={indicators.slice(0, 16)}
  >
    {itemProps => (
      <GridListItem
        {...itemProps}
        isDisabled={itemProps.item && itemProps.item.isDisabled}
        enableItem={() => {}}
        disableItem={() => {}}
      />
    )}
  </GridList>
</div>
<MDXTag name="h4" components={components} props={{"id":"gridlist-props"}}>{`GridList props`}</MDXTag>
<PropsTable of={GridList} />
